import osimport sysfrom pymxs import runtime as rtfrom maxsdk import menu as sdkmenureload(sdkmenu)toolFolder = os.path.dirname(__file__)if toolFolder not in sys.path:    sys.path.append(toolFolder)import Utilities.updateContainerID as updateContainerIDreload(updateContainerID)CATEGORY_MACRO = "FlightSim"SUB_MENU_NAME = "Babylon Utilities"def installMenu():    sdkmenu.createMacroScript(_func=updateContainerID.run, category="FlightSim", name="UpdateContainerID", button_text= "Update Container ID")    actionItem0 = rt.menuMan.createActionItem("UpdateContainerID", "FlightSim")    FlightSimMenu = rt.menuMan.findMenu(CATEGORY_MACRO)    if not FlightSimMenu:        FlightSimMenu = rt.menuMan.createMenu(CATEGORY_MACRO)    BabylonUtilitesMenu = rt.menuMan.findMenu(SUB_MENU_NAME)    if not BabylonUtilitesMenu:        BabylonUtilitesMenu = rt.menuMan.createMenu(SUB_MENU_NAME)    BabylonUtilitesItem = rt.menuMan.createSubMenuItem(SUB_MENU_NAME, BabylonUtilitesMenu)    sdkmenu.safeAddItem(BabylonUtilitesMenu, actionItem0)    sdkmenu.safeAddItem(FlightSimMenu, BabylonUtilitesItem)    rt.menuMan.updateMenuBar()